import React, { useState, useEffect } from "react";
import { Box } from "rebass";

const userId = localStorage.getItem('USER_ID')

const AccountPage = () => {
  const [user, updateUser] = useState(undefined);

  useEffect(() => {
    fetchUser();
  });

  const fetchUser = () => {
  	Api.fetchUser({ id: userId }, onSuccess, onFailure)
  };

  const onSuccess = (response) => {
  	updateUser(response.data['user'])
  }

  const onFailure = (error) => {
  	message.error('Failed to fetch user details')
  }

  const onChange = (key, e) => {
  	let data = user
    if (key === "fname") {
      data['first_name'] = e.target.value
    } else if (key === "lname") {
    	data['last_name'] = e.target.value
    } else if (key === "email") {
    	data['email'] = e.target.value
    } else if (key === "address") {
    	data['address'] = e.target.value
    } else if (key === "city") {
    	data['city'] = e.target.value
    } else if (key === "state") {
    	data['state'] = e.target.value
    } else if (key === "zip") {
    	data['zip'] = e.target.value
    }
    updateUser(user)
  };

  const onTagsChange = (tags) => {
    let uniqueSet = [...new Set(tags)];
    updateTags(uniqueSet);
  };

  if (!user) { return null }

  return (
    <Box>
      <Input
        type="text"
        placeholder="First Name*"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("fname", e)}
        value={user['first_name']}
      />
      <Input
        type="text"
        placeholder="Last Name"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("lname", e)}
        value={user['last_name']}
      />
      <Input
        type="text"
        placeholder="Email*"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("email", e)}
        value={user['email']}
        disabled={user["id"] ? true : false}
      />
      <Input
        type="text"
        placeholder="Address"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("address", e)}
        value={user['address']}
      />
      <Input
        type="text"
        placeholder="City"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("city", e)}
        value={user['city']}
      />
      <Input
        type="text"
        placeholder="State"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("state", e)}
        value={user['state']}
      />
      <Input
        type="text"
        placeholder="Zip"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("zip", e)}
        value={user['zip']}
      />
      <Select
        mode="multiple"
        placeholder="Tags"
        defaultValue={user['user_tags']}
        style={{ width: "100%", height: 40 }}
        onChange={onTagsChange}
      >
        {tags.map((tag) => (
          <Option value={tag.id}>{tag.name}</Option>
        ))}
      </Select>
    </Box>
  );
};

export default AccountPage;
