import React, { useState, useEffect } from "react";
import { Box } from "rebass";

const userId = localStorage.getItem('USER_ID')

const AccountPage = () => {
  const [user, updateUser] = useState(undefined);

  useEffect(() => {
    fetchUser();
  });

  const fetchUser = () => {
  	Api.fetchUser({ id: userId }, onSuccess, onFailure)
  };

  const onSuccess = (response) => {
  	updateUser(response.data['user'])
  }

  const onFailure = (error) => {
  	message.error('Failed to fetch user details')
  }

  const onChange = (key, e) => {
    if (key === "fname") {
      updateFname(e.target.value);
    } else if (key === "lname") {
      updateLname(e.target.value);
    } else if (key === "email") {
      updateEmail(e.target.value);
    } else if (key === "address") {
      updateAddress(e.target.value);
    } else if (key === "city") {
      updateCity(e.target.value);
    } else if (key === "state") {
      updateState(e.target.value);
    } else if (key === "zip") {
      updateZip(e.target.value);
    }
  };

  const onTagsChange = (tags) => {
    let uniqueSet = [...new Set(tags)];
    updateTags(uniqueSet);
  };

  if (!user) { return null }

  return (
    <Box>
      <Input
        type="text"
        placeholder="First Name*"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("fname", e)}
        value={first_name}
      />
      <Input
        type="text"
        placeholder="Last Name"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("lname", e)}
        value={last_name}
      />
      <Input
        type="text"
        placeholder="Email*"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("email", e)}
        value={email}
        disabled={user["id"] ? true : false}
      />
      <Input
        type="text"
        placeholder="Address"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("address", e)}
        value={address}
      />
      <Input
        type="text"
        placeholder="City"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("city", e)}
        value={city}
      />
      <Input
        type="text"
        placeholder="State"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("state", e)}
        value={state}
      />
      <Input
        type="text"
        placeholder="Zip"
        style={{ marginBottom: 20, height: 40 }}
        onChange={(e) => onChange("zip", e)}
        value={zip}
      />
      <Select
        mode="multiple"
        placeholder="Tags"
        defaultValue={user_tags}
        style={{ width: "100%", height: 40 }}
        onChange={onTagsChange}
      >
        {tags.map((tag) => (
          <Option value={tag.id}>{tag.name}</Option>
        ))}
      </Select>
    </Box>
  );
};

export default AccountPage;
