import React, { useState } from "react";
import axios from "axios";
import {
  storeToken,
  storeStaff
} from "../Auth";
import { HOST } from "../Url";
import { Icon, Input, Button } from "antd";
import { Redirect } from "react-router-dom";
import { Box } from 'rebass';

const LoginForm = () => {

  const [loggedIn, updateLoggedIn] = useState(false)
  const [email, updateEmail] = useState(undefined)
  const [password, updatePassword] = useState(undefined)

  const handleLogin = () => {
    axios
      .post(HOST + "/sign_in", { email: email, password: password })
      .then(function(response) {
        let key = response.headers["USER-TOKEN"];
        if (key !== undefined && key.length > 20) {
          storeToken(response.headers["USER-TOKEN"]);
          storeStaff("USER_ID", response.data["user"]["id"]);
          storeStaff("USER_NAME", response.data["user"]["name"]);
          storeStaff("USER_EMAIL", response.data["user"]["email"]);
          updateLoggedIn(true)
        }
      })
      .catch(function(error) {
        if (error.response) {
          console.log(error.response.status);
        }
      });
  }

  const handleSubmit = (e) => {

  };

  const changeUserName = (e) => {
    updateEmail(e.target.value)
  }

  const changePassword = (e) => {
    updatePassword(e.target.value)
  }

  const Form = () => {
    return(
      <Box px='20px'>
        <h3>Login</h3>
        <Input type='username' onChange={changeUserName} style={{height: 45}}/>
        <br />
        <br />
        <Input.Password type='password' onChange={changePassword} style={{height: 45}} />
      </Box>  
    )
  }

  if (loggedIn) {
    return <Redirect to={"/users"} />
  }

  return <Form />
}

export default LoginForm;
